/**
 * @title ERC20 token info interface
 * @author Superfluid
 * @dev ERC20 standard interface does not specify these functions, but
 *      often the token implementations have them.
 */
interface TokenInfo {
    /**
     * @dev Returns the name of the token.
     */
    function name() external view returns (string memory);

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() external view returns (string memory);

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` tokens should
     * be displayed to a user as `5,05` (`505 / 10 ** 2`).
     *
     * Tokens usually opt for a value of 18, imitating the relationship between
     * Ether and Wei. This is the value {ERC20} uses, unless {_setupDecimals} is
     * called.
     *
     * NOTE: This information is only used for _display_ purposes: it in
     * no way affects any of the arithmetic of the contract, including
     * {IERC20-balanceOf} and {IERC20-transfer}.
     */
    function decimals() external view returns (uint8);
}
","keccak256":"0x7456cd394cc905aae7cdbd4601ce1c296c768c13c28e66efbb913cc070bad3e4","license":"AGPLv3"},"contracts/Guild.sol":{"content":"// SPDX-License-Identifier: AGPLv3
pragma solidity 0.8.16;
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {IGuild} from "./interfaces/IGuild.sol";
import {AddressArrayUtils} from "./libraries/ArrayUtils.sol";
import {GuildRegistry} from "./GuildRegistry.sol";
import {World} from "./World.sol";

contract Guild is IGuild, AccessControl {
    using AddressArrayUtils for address[];

    bytes32 public constant LEADER = keccak256("LEADER");
    bytes32 public constant TREASURY_MANAGER = keccak256("TREASURY_MANAGER");
    bytes32 public constant OFFICER = keccak256("OFFICER");
    bytes32 public constant MEMBER = keccak256("MEMBER");

    string private s_name;
    address private s_leader;
    address[] private s_memberList;
    address[] private s_recruitmentRequests;

    GuildRegistry private s_guildRegistry;

    mapping(uint256 => bytes32) private s_roles;

    constructor(GuildRegistry registry, string memory name) {
        s_name = name;
        s_leader = msg.sender;
        s_guildRegistry = registry;

        _setRoleAdmin(LEADER, LEADER);
        _setRoleAdmin(OFFICER, LEADER);
        _setRoleAdmin(TREASURY_MANAGER, LEADER);
        _setRoleAdmin(MEMBER, OFFICER);

        _setupRole(LEADER, s_leader);

        s_roles[uint256(Role.LEADER)] = LEADER;
        s_roles[uint256(Role.TREASURY_MANAGER)] = TREASURY_MANAGER;
        s_roles[uint256(Role.OFFICER)] = OFFICER;
        s_roles[uint256(Role.MEMBER)] = MEMBER;

        s_memberList.push(s_leader);

        s_guildRegistry.register();
    }

    function requestJoin(address candidate) external {
        s_recruitmentRequests.push(candidate);

        emit Guild_JoinRequested(candidate);
    }

    function denyRequest(address candidate) external {
        if (!hasRole(LEADER, msg.sender) || !hasRole(OFFICER, msg.sender)) {
            revert IGuild_Unauthorized();
        }

        (bool found, uint256 index) = s_recruitmentRequests.indexOf(candidate);

        if (found) {
            s_recruitmentRequests.remove(index);

            emit Guild_JoinDenied(candidate);
        } else {
            revert IGuild_CandidateNotFound();
        }
    }

    function recruit(address candidate) external {
        if (!hasRole(LEADER, msg.sender) || !hasRole(OFFICER, msg.sender)) {
            revert IGuild_Unauthorized();
        }

        if (!s_recruitmentRequests.contains(candidate)) {
            revert IGuild_CandidateNotFound();
        }

        (bool member, address guild) = s_guildRegistry.isMemberOfAnyGuild(
            candidate
        );

        if (member) {
            revert IGuild_AlreadyMemberOfAnotherGuild(guild);
        }

        (bool found, uint256 index) = s_recruitmentRequests.indexOf(candidate);

        if (found) {
            s_recruitmentRequests.remove(index);
        }

        _setupRole(MEMBER, candidate);
        s_memberList.push(candidate);
        s_guildRegistry.registerMember(candidate);

        emit Guild_JoinAccepted(candidate);
    }

    function expel(address member) external onlyRole(LEADER) {
        if (member == s_leader) {
            revert IGuild_CannotExpelLeader();
        }

        (bool found, uint256 index) = s_memberList.indexOf(member);

        if (found) {
            s_memberList.remove(index);

            bytes32 currentRole = getRole(member);
            _revokeRole(currentRole, member);

            s_guildRegistry.unregisterMember(member);

            emit Guild_MemberExpelled(member);
        } else {
            revert IGuild_NotAMember();
        }
    }

    function assignRole(Role role, address member) external onlyRole(LEADER) {
        if (member == s_leader) {
            revert IGuild_CannotChangeLeaderRole();
        }

        bytes32 currentRoleName = getRole(member);
        bytes32 targetRoleName = s_roles[uint256(role)];

        if (currentRoleName == targetRoleName) {
            revert IGuild_AlreadyInRole();
        }

        _revokeRole(currentRoleName, member);
        _setupRole(targetRoleName, member);

        emit Guild_RoleAssigned(member, role);
    }

    function transferLeadership(address newLeader) external onlyRole(LEADER) {
        (bool found, uint256 index) = s_memberList.indexOf(s_leader);

        if (!found) {
            revert IGuild_NewLeaderMustBeInGuild();
        }

        if (newLeader != s_leader) {
            revert IGuild_NewLeaderMustBeDifferent();
        }

        s_memberList.remove(index);

        address oldLeader = s_leader;
        s_leader = newLeader;

        _setupRole(LEADER, s_leader);
        _revokeRole(LEADER, msg.sender);

        emit Guild_LeadershipTransferred(oldLeader, newLeader);
    }

    function disband() external onlyRole(LEADER) {
        for (uint256 i = 0; i < s_memberList.length; i++) {
            address member = s_memberList[i];
            bytes32 currentRole = getRole(member);
            _revokeRole(currentRole, member);
            s_guildRegistry.unregisterMember(member);
        }

        s_memberList = new address[](0);
        s_guildRegistry.unregister();

        emit Guild_Disbanded();

        selfdestruct(payable(s_leader));
    }

    function isMember(address account) external view returns (bool) {
        return s_memberList.contains(account);
    }

    function isLeader(address user) external view returns (bool) {
        return user == s_leader;
    }

    function getName() external view returns (string memory) {
        return s_name;
    }

    function getLeader() external view returns (address) {
        return s_leader;
    }

    function getMembers() external view returns (address[] memory) {
        return s_memberList;
    }

    function getRecruitmentRequests() external view returns (address[] memory) {
        return s_recruitmentRequests;
    }

    function getRole(address user) public view returns (bytes32) {
        unchecked {
            for (uint256 i = 0; i < uint256(Role.LEADER) + 1; i++) {
                if (hasRole(s_roles[i], user)) {
                    return s_roles[i];
                }
            }

            return bytes32(0);
        }
    }
}
","keccak256":"0x99d37dcd3cb9f9574bb16e27be14927e5af04f74339eedbb0eed5db2ec407ab3","license":"AGPLv3"},"contracts/GuildRegistry.sol":{"content":"// SPDX-License-Identifier: AGPLv3
pragma solidity 0.8.16;
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {AddressArrayUtils} from "./libraries/ArrayUtils.sol";
import {IGuildRegistry} from "./interfaces/IGuildRegistry.sol";
import {Guild} from "./Guild.sol";

contract GuildRegistry is IGuildRegistry, Ownable {
    using AddressArrayUtils for address[];

    address[] s_foundingRequests;
    address[] public s_guilds;
    mapping(address => address) public s_members;

    uint256 private s_reputation = 0;

    modifier onlyGuilds() {
        if (!s_guilds.contains(msg.sender)) {
            revert IGuildRegistry_OnlyAllowedForGuilds();
        }
        _;
    }

    constructor() Ownable() {}

    function register() public onlyGuilds {
        if (s_foundingRequests.contains(msg.sender)) {
            revert IGuildRegistry_GuildAlreadyExists();
        }

        s_foundingRequests.push(msg.sender);
        address leader = Guild(msg.sender).getLeader();

        emit GuildRegistry_GuildEvent(
            leader,
            msg.sender,
            GuildEventType.REGISTER_REQUEST_RECEIVED
        );
    }

    function unregister() public onlyGuilds {
        (bool found, uint256 index) = s_guilds.indexOf(msg.sender);

        if (found) {
            s_guilds.remove(index);
        }

        address leader = Guild(msg.sender).getLeader();

        emit GuildRegistry_GuildEvent(
            leader,
            msg.sender,
            GuildEventType.UNREGISTER_REQUEST_RECEIVED
        );
    }

    function accept(address guild) public onlyOwner {
        if (s_guilds.contains(guild)) {
            revert IGuildRegistry_GuildAlreadyRegistered();
        }

        (bool found, uint256 index) = s_foundingRequests.indexOf(guild);

        if (found) {
            s_foundingRequests.remove(index);
        }

        s_guilds.push(guild);
        address leader = Guild(guild).getLeader();

        emit GuildRegistry_GuildEvent(
            leader,
            guild,
            GuildEventType.REGISTER_REQUEST_ACCEPTED
        );
    }

    function registerMember(address candidate) public onlyGuilds {
        (bool member, address guild) = isMemberOfAnyGuild(candidate);

        if (member) {
            revert IGuildRegistry_AlreadyMemberOfAnotherGuild(guild);
        }

        if (Guild(msg.sender).isMember(candidate)) {
            s_members[candidate] = msg.sender;
        }

        emit GuildRegistry_GuildEvent(
            candidate,
            msg.sender,
            GuildEventType.REGISTER_MEMBER
        );
    }

    function unregisterMember(address member) public onlyGuilds {
        if (s_members[member] == msg.sender) {
            s_members[member] = address(0);
        }

        emit GuildRegistry_GuildEvent(
            member,
            msg.sender,
            GuildEventType.UNREGISTER_MEMBER
        );
    }

    function isMemberOfAnyGuild(
        address user
    ) public view returns (bool, address) {
        address guild = s_members[user];

        return (s_guilds.contains(guild), guild);
    }
}
","keccak256":"0x44e1206a11c1f8ee9e6878178803f780472e93bea26a2c66a8c994f877818516","license":"AGPLv3"},"contracts/ManaReserve.sol":{"content":"// SPDX-License-Identifier: AGPLv3
pragma solidity ^0.8.16;

import {ISuperfluid, ISuperToken, ISuperApp} from "@superfluid-finance/ethereum-contracts/contracts/interfaces/superfluid/ISuperfluid.sol";
import {IManaReserve} from "./interfaces/IManaReserve.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {SuperTokenV1Library} from "@superfluid-finance/ethereum-contracts/contracts/apps/SuperTokenV1Library.sol";
import {World} from "./World.sol";

contract ManaReserve is IManaReserve, Ownable {
    using SuperTokenV1Library for ISuperToken;

    bool private isConnected = false;
    int96 private constant INITIAL_FLOWRATE = 1;

    ISuperToken private MANAx;

    modifier requireConnected() {
        if (!isConnected) {
            revert WorldNotConnected();
        }
        _;
    }

    constructor(address world) Ownable() {
        _transferOwnership(world);
    }

    function connectWorld(ISuperToken mana) external onlyOwner {
        MANAx = mana;
        MANAx.createFlow(msg.sender, INITIAL_FLOWRATE);

        isConnected = true;
    }

    function updateManaFlow(
        int96 flowIncrease
    ) external onlyOwner requireConnected {
        MANAx.updateFlow(msg.sender, flowIncrease);
    }
}
