pragma solidity ^0.8.16;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ISuperToken} from "@superfluid-finance/ethereum-contracts/contracts/interfaces/superfluid/ISuperfluid.sol";
import {ManaReserve} from "./ManaReserve.sol";
import {Player} from "./tokens/Player.sol";
import {IWorld} from "./interfaces/IWorld.sol";
import {GuildRegistry} from "./GuildRegistry.sol";

contract World is IWorld, Ownable {
    bool private s_initialized = false;

    Player private s_player;
    ISuperToken private s_manaX;
    ManaReserve private s_manaReserve;
    GuildRegistry private s_guildRegistry;

    modifier ready() {
        if (!s_initialized) {
            revert NotInitialized();
        }
        _;
    }

    modifier onlyPlayer() {
        if (msg.sender != address(s_player)) {
            revert Unauthorized();
        }
        _;
    }

    constructor() Ownable() {}

    function initialize(
        Player player,
        ISuperToken mana,
        ManaReserve manaReserve,
        GuildRegistry guildRegistry
    ) external onlyOwner {
        s_player = player;
        s_manaX = mana;
        s_manaReserve = manaReserve;
        s_guildRegistry = guildRegistry;

        manaReserve.connectWorld(mana);

        s_initialized = true;
    }
}
","keccak256":"0xae655c935727d6a96b2fb88560d481fc3d829c5a505c0521be1a100d12eb29f3","license":"AGPLv3"},"contracts/interfaces/IGuild.sol":{"content":"// SPDX-License-Identifier: AGPLv3
pragma solidity ^0.8.16;

interface IGuild {
    error IGuild_AlreadyInRole();
    error IGuild_AlreadyMemberOfAnotherGuild(address guild);
    error IGuild_CandidateNotFound();
    error IGuild_CannotExpelLeader();
    error IGuild_CannotChangeLeaderRole();
    error IGuild_InvalidRole();
    error IGuild_NewLeaderMustBeDifferent();
    error IGuild_NewLeaderMustBeInGuild();
    error IGuild_NotAMember();
    error IGuild_NotInRole();
    error IGuild_Unauthorized();

    enum Role {
        MEMBER,
        OFFICER,
        TREASURY_MANAGER,
        LEADER
    }

    event Guild_Disbanded();
    event Guild_JoinAccepted(address indexed candidate);
    event Guild_JoinDenied(address indexed candidate);
    event Guild_JoinRequested(address indexed candidate);
    event Guild_LeadershipTransferred(
        address indexed oldLeader,
        address indexed newLeader
    );
    event Guild_MemberExpelled(address indexed member);
    event Guild_RoleAssigned(address indexed member, Role indexed role);
}
